int F(char m[][40], int X){
    int cont = 0;
    char* limi = *(m + X-2);
    for (char* a = *m;a!=limi; a++) {
        if (*a=='c'&&*(a+1)=='p'&&*(a+2)=='p') {
            cont++;
        }
    }
        for (char* b = *m;b!=*(m+X-2);b++) {
            if (*b == 'c' && *(b + X) == 'p' && *(b + (X * 2)) == 'p') {
                cont++;
            }
        }
    return cont;
}
